descriptor.controllerScript:
self.onInit = function() {
    const ctx = document.getElementById("leadTimeChart").getContext("2d");

    const leadTimeChart = new Chart(ctx, {
        type: "bar",
        data: {
            labels: [], // Dates will be added here
            datasets: [{
                label: "Lead Time (%)",
                backgroundColor: [],
                borderColor: "#ddd",
                data: [] // Lead time values will be added here
            }]
        },
        options: {
            scales: {
                y: {
                    beginAtZero: true,
                    max: 100,
                    title: {
                        display: true,
                        text: "Lead Time (%)"
                    }
                }
            },
            plugins: {
                legend: {
                    display: false
                }
            }
        }
    });

    function addData(date, leadTime) {
        leadTimeChart.data.labels.push(date);
        leadTimeChart.data.datasets[0].data.push(leadTime);
        leadTimeChart.data.datasets[0].backgroundColor.push(leadTime >= 50 ? "green" : "red");
        leadTimeChart.update();
    }

    function simulateData() {
        const now = new Date();
        const leadTime = Math.floor(Math.random() * 100);
        const date = now.toLocaleTimeString();

        addData(date, leadTime);
    }

    // Initial data and update every minute for demonstration
    simulateData();
    setInterval(simulateData, 60000); // Update every 1 minute
};

