descriptor.templateCss:
.worker-table {
    font-family: Arial, sans-serif;
    margin: 10px;
}

.worker-table h3 {
    text-align: center;
    color: #333;
}

table {
    width: 100%;
    border-collapse: collapse;
}

th, td {
    border: 1px solid #ddd;
    padding: 8px;
    text-align: center;
}

th {
    background-color: #f2f2f2;
    font-weight: bold;
}

.status-on {
    color: green;
}

.status-off {
    color: red;
}

descriptor.controllerScript:
self.onInit = function() {
    // HTML Structure for the Table
    self.ctx.$container.html(`
        <div class="worker-table">
            <h3>Worker Safety Protocols</h3>
            <table>
                <thead>
                    <tr>
                        <th>Worker Name</th>
                        <th>Position</th>
                        <th>Helmet</th>
                        <th>Gloves</th>
                        <th>Vest</th>
                        <th>Boots</th>
                    </tr>
                </thead>
                <tbody id="worker-list"></tbody>
            </table>
        </div>
    `);

    // Array of worker data
    const workers = [
        { name: "John Doe", position: "Electrician" },
        { name: "Jane Smith", position: "Welder" },
        { name: "Mike Johnson", position: "Safety Officer" },
        { name: "Emily Davis", position: "Supervisor" }
    ];

    // Generate random status function
    function getRandomStatus() {
        return Math.random() > 0.5 ? "On" : "Off";
    }

    // Populate the worker table
    function populateWorkerTable() {
        const tableBody = self.ctx.$container.find("#worker-list"); // Using self.ctx.$container for DOM access

        if (!tableBody.length) {
            console.error("Table body with ID 'worker-list' not found.");
            return;
        }

        tableBody.empty(); // Clear existing content

        workers.forEach(worker => {
            const row = `<tr>
                <td>${worker.name}</td>
                <td>${worker.position}</td>
                <td class="status ${getRandomStatus() === 'On' ? 'status-on' : 'status-off'}">${getRandomStatus()}</td>
                <td class="status ${getRandomStatus() === 'On' ? 'status-on' : 'status-off'}">${getRandomStatus()}</td>
                <td class="status ${getRandomStatus() === 'On' ? 'status-on' : 'status-off'}">${getRandomStatus()}</td>
                <td class="status ${getRandomStatus() === 'On' ? 'status-on' : 'status-off'}">${getRandomStatus()}</td>
            </tr>`;
            tableBody.append(row); // Append row to table body
        });
    }

    // Initialize and populate the table
    populateWorkerTable();
};

