descriptor.templateCss:
.map-widget {
  font-family: Arial, sans-serif;
  text-align: center;
  padding: 10px;
}

.map-area {
  position: relative;
  width: 300px;
  height: 300px;
  background-color: #e0f7e0; /* Light green background for general allowed area */
  border: 2px solid #333;
  margin: 0 auto;
}

.zone {
  position: absolute;
  padding: 10px;
  border-radius: 5px;
}

.allowed {
  background-color: rgba(34, 139, 34, 0.2); /* Green for allowed areas */
  border: 1px solid green;
}

.restricted {
  background-color: rgba(255, 0, 0, 0.2); /* Red for restricted areas */
  border: 1px solid red;
}

.worker {
  width: 15px;
  height: 15px;
  border-radius: 50%;
  display: inline-block;
  margin: 3px;
}

.green {
  background-color: green;
}

.red {
  background-color: red;
}

