descriptor.templateCss:
.custom-widget {
    font-family: Arial, sans-serif;
    margin: 20px;
    overflow-x: auto; /* Enables horizontal scrolling on smaller screens */
}

#staticDataTable {
    width: 100%; /* Table takes full width of its container */
    border-collapse: collapse;
    table-layout: auto; /* Allows the table to resize dynamically */
}

#staticDataTable th, #staticDataTable td {
    border: 1px solid #ddd;
    padding: 8px;
    text-align: left;
}

#staticDataTable th {
    background-color: #f2f2f2;
}

/* Optional: Media queries for smaller screens */
@media (max-width: 600px) {
    #staticDataTable th, #staticDataTable td {
        padding: 5px; /* Adjust padding for smaller screens */
        font-size: 14px; /* Smaller font size */
    }
}

