self.onInit = function() {
    // Check if Plotly is loaded and available
    if (typeof Plotly !== 'undefined') {
        var data = [
            {
                type: 'bar',
                x: [4, 7, 55, 172, 0, 6],  // Data for "Open" alerts
                y: ['Fall Prevention', 'Fire', 'Caught Detection', 'Out of Geofence', 'Free Fall', 'SOS'],
                orientation: 'h',  // Horizontal bars
                name: 'Open',
                marker: {
                    color: '#ff7043'
                }
            },
            {
                type: 'bar',
                x: [0, 0, 0, 112, 0, 0],  // Data for "Assigned Open" alerts
                y: ['Fall Prevention', 'Fire', 'Caught Detection', 'Out of Geofence', 'Free Fall', 'SOS'],
                orientation: 'h',
                name: 'Assigned Open',
                marker: {
                    color: '#29b6f6'
                }
            },
            {
                type: 'bar',
                x: [0, 0, 55, 212, 0, 0],  // Data for "Resolved" alerts
                y: ['Fall Prevention', 'Fire', 'Caught Detection', 'Out of Geofence', 'Free Fall', 'SOS'],
                orientation: 'h',
                name: 'Resolved',
                marker: {
                    color: '#66bb6a'
                }
            }
        ];

        var layout = {
            title: 'Alerts Status Count by Category',
            barmode: 'stack',  // Stack bars on top of each other
            margin: {
                l: 150  // Adjust left margin to fit long labels
            },
            xaxis: {
                title: 'Alert Counts'
            },
            yaxis: {
                automargin: true
            },
            showlegend: true,
            legend: {
                x: 1,
                y: 1
            }
        };

        // Render the bar chart using Plotly
        Plotly.newPlot('barChart', data, layout);

    } else {
        console.error('Plotly is not loaded.');  // If Plotly is not loaded, log an error
    }
};

self.onDataUpdated = function() {
    // Optionally, you can dynamically update the data here
};
