descriptor.templateCss:
.site-map {
    font-family: Arial, sans-serif;
    margin: 10px;
    display: flex; /* Use flexbox for horizontal alignment */
    gap: 20px; /* Space between each site */
    justify-content: center;
}

.site {
    position: relative;
    border: 2px solid #ddd;
    padding: 20px;
    background-color: #d0f5d8; /* Green for allowed areas */
    text-align: center;
    width: 250px; /* Increased width */
    height: 200px; /* Increased height */
}

.site .restricted-area {
    width: 80%;
    height: 60px; /* Adjusted height for larger site */
    background-color: #ffe0e0; /* Red for restricted areas */
    border: 1px solid red;
    position: absolute;
    bottom: 10px;
    left: 10%;
}

.site-label {
    font-weight: bold;
    margin-bottom: 10px;
}

/* Worker styling */
.worker {
    width: 15px; /* Increased worker size */
    height: 15px; /* Increased worker size */
    border-radius: 50%;
    position: absolute;
    cursor: pointer;
}

/* Allowed worker style */
.allowed-worker {
    background-color: green;
    animation: moveWithinBounds 6s infinite alternate ease-in-out;
}

/* Prohibited worker style */
.prohibited-worker {
    background-color: red;
    animation: none;
}

/* Tooltip styling */
.worker:hover::after {
    content: attr(data-info);
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    padding: 4px 8px;
    background-color: black;
    color: white;
    font-size: 10px;
    border-radius: 4px;
    white-space: nowrap;
    pointer-events: none;
}

/* Animation for allowed workers within bounds */
@keyframes moveWithinBounds {
    0% { transform: translate(0, 0); }
    25% { transform: translate(10px, -5px); }
    50% { transform: translate(-10px, 5px); }
    75% { transform: translate(5px, -10px); }
    100% { transform: translate(-5px, 10px); }
}

descriptor.controllerScript:
self.onInit = function() {
    console.log("Map loaded with constrained worker movement.");

    // Randomize movement for allowed workers
    document.querySelectorAll('.allowed-worker').forEach(worker => {
        const duration = Math.random() * 5 + 3; // Duration between 3 and 8 seconds
        const delay = Math.random() * 2; // Delay between 0 and 2 seconds
        worker.style.animationDuration = `${duration}s`;
        worker.style.animationDelay = `${delay}s`;
    });

    // Prevent prohibited workers from moving
    document.querySelectorAll('.prohibited-worker').forEach(worker => {
        worker.style.animation = 'none'; // Disable animation for prohibited workers
    });
};

