/* Chart Container Style */
   .chart-container {
       position: relative;
       width: 100%;
       height: 300px; max-width: 600px; /* Set maximum width to fit properly in widget area */
       margin: auto;
       padding: 20px;
       background: #ffffff; /* White background for the chart container */
       border-radius: 5px; /* Small rounded corners */
       box-shadow: 0 0 15px rgba(0, 0, 0, 0.15); /* Add light shadow for better appearance */
   }

   /* Canvas Chart Style */
   #securityChart {
       width: 100% !important; /* Set width to fit the widget */
       height: auto !important; /* Adjust height proportionally */
       border-radius: 0px; /* Remove rounded corners */
       background: none; /* Remove background color */
       box-shadow: none; /* Remove shadow */
   }

   /* Chart Title Style */
   .chart-title {
       font-size: 1.2em;
       text-align: center;
       margin-bottom: 15px;
       color: #333;
       font-weight: bold;
   }

descriptor.controllerScript:
self.onInit = function() {
    // Sample data representing current security status
    const currentData = [75, 80, 85, 45, 90, 95, 40, 80, 92, 88, 85];
    // Sample data representing forecasted security status
    const forecastData = [78, 82, 87, 50, 93, 97, 55, 83, 95, 90, 87];

    // Labels for the X-axis representing time periods (e.g., days or weeks)
    const labels = ['Day 1', 'Day 2', 'Day 3', 'Day 4', 'Day 5', 'Day 6', 'Day 7', 'Day 8', 'Day 9', 'Day 10', 'Day 11'];

    // Get the canvas context to draw the chart
    const ctx = document.getElementById('securityChart').getContext('2d');

    // Create the chart using Chart.js
    const securityChart = new Chart(ctx, {
        type: 'line',
        data: {
            labels: labels,
            datasets: [
                {
                    label: 'Current Security Status',
                    data: currentData,
                    borderColor: '#007bff',
                    backgroundColor: 'rgba(0, 123, 255, 0.2)',
                    borderWidth: 3,
                    fill: true,
                    tension: 0.4,
                    pointBackgroundColor: function(context) {
                        // Highlight points where the value is below 50% with a red color
                        const value = context.raw;
                        return value < 50 ? '#ff0000' : '#007bff';
                    },
                    pointRadius: function(context) {
                        // Increase the radius for points where the value is below 50%
                        const value = context.raw;
                        return value < 50 ? 8 : 5;
                    },
                    pointHoverRadius: 10, // Increase the radius when hovering over the point
                    hoverBorderWidth: 3 // Make the border thicker when hovering
                },
                {
                    label: 'Forecasted Security Status',
                    data: forecastData,
                    borderColor: '#ff7f00',
                    backgroundColor: 'rgba(255, 127, 0, 0.2)',
                    borderWidth: 3,
                    fill: true,
                    tension: 0.4,
                    pointBackgroundColor: function(context) {
                        // Highlight points where the value is below 50% with a red color
                        const value = context.raw;
                        return value < 50 ? '#ff0000' : '#ff7f00';
                    },
                    pointRadius: function(context) {
                        // Increase the radius for points where the value is below 50%
                        const value = context.raw;
                        return value < 50 ? 8 : 5;
                    },
                    pointHoverRadius: 10, // Increase the radius when hovering over the point
                    hoverBorderWidth: 3 // Make the border thicker when hovering
                }
            ]
        },
        options: {
            responsive: true,
            maintainAspectRatio: false,
            animation: {
                duration: 1500, // Smooth animation for the lines
                easing: 'easeInOutQuart' // Easing function for better visual appeal
            },
            interaction: {
                mode: 'nearest',
                axis: 'x',
                intersect: false
            },
            scales: {
                y: {
                    beginAtZero: true,
                    title: {
                        display: true,
                        text: 'Security Alert Level (%)',
                        font: {
                            size: 15,
                            weight: 'bold'
                        },
                        color: '#333'
                    },
                    grid: {
                        display: true,
                        color: '#e0e0e0'
                    },
                    ticks: {
                        callback: function(value) {
                            return value + '%';
                        },
                        font: {
                            size: 12,
                            weight: 'bold'
                        },
                        color: '#333'
                    }
                },
                x: {
                    title: {
                        display: true,
                        text: 'Time Period',
                        font: {
                            size: 15,
                            weight: 'bold'
                        },
                        color: '#333'
                    },
                    grid: {
                        display: false
                    },
                    ticks: {
                        font: {
                            size: 12,
                            weight: 'bold'
                        },
                        color: '#333'
                    }
                }
            },
            plugins: {
                legend: {
                    display: true,
                    position: 'top',
                    labels: {
                        font: {
                            size: 13,
                            weight: 'bold'
                        },
                        color: '#333'
                    }
                },
                tooltip: {
                    enabled: true,
                    mode: 'index',
                    intersect: false,
                    backgroundColor: '#f5f5f5',
                    titleColor: '#333',
                    bodyColor: '#333',
                    borderColor: '#007bff',
                    borderWidth: 1,
                    titleFont: {
                        size: 14,
                        weight: 'bold'
                    },
                    bodyFont: {
                        size: 12
                    }
                }
            }
        }
    });
};

