.worker-table {
    width: 100%;
    font-family: Arial, sans-serif;
    font-size: 14px;
    color: #333;
}

.worker-table h3 {
    margin-bottom: 10px;
}

.worker-table table {
    width: 100%;
    border-collapse: collapse;
}

.worker-table th, .worker-table td {
    padding: 8px;
    text-align: center;
    border: 1px solid #ddd;
}

.worker-table th {
    background-color: #f4f4f4;
}

.status-on {
    color: green;
    font-weight: bold;
}

.status-off {
    color: red;
    font-weight: bold;
}

descriptor.controllerScript:
document.addEventListener("DOMContentLoaded", function() {
    // Array of random worker names and positions
    const workers = [
        { name: "John Doe", position: "Electrician" },
        { name: "Jane Smith", position: "Welder" },
        { name: "Mike Johnson", position: "Safety Officer" },
        { name: "Emily Davis", position: "Supervisor" }
    ];

    // Function to generate random compliance status (On/Off)
    function getRandomStatus() {
        return Math.random() > 0.5 ? "On" : "Off";
    }

    // Populate table with worker data
    function populateWorkerTable() {
        const tableBody = document.getElementById("worker-list");
        if (!tableBody) return;  // Check if the element exists

        tableBody.innerHTML = ""; // Clear previous content

        workers.forEach(worker => {
            const row = document.createElement("tr");

            // Name and Position columns
            row.innerHTML = `
                <td>${worker.name}</td>
                <td>${worker.position}</td>
                <td class="status ${getRandomStatus() === 'On' ? 'status-on' : 'status-off'}">${getRandomStatus()}</td>
                <td class="status ${getRandomStatus() === 'On' ? 'status-on' : 'status-off'}">${getRandomStatus()}</td>
                <td class="status ${getRandomStatus() === 'On' ? 'status-on' : 'status-off'}">${getRandomStatus()}</td>
                <td class="status ${getRandomStatus() === 'On' ? 'status-on' : 'status-off'}">${getRandomStatus()}</td>
            `;

            tableBody.appendChild(row);
        });
    }

    // Initialize the table on page load
    populateWorkerTable();
});

