descriptor.templateCss:
.status-card-container {
    display: flex;
    justify-content: center;
    width: 100%;
    max-width: 300px;
    margin: auto;
}

.status-card {
    width: 100%;
    padding: 15px;
    font-family: Arial, sans-serif;
    color: #333;
    display: flex;
    background-color: transparent; /* Transparent background */
}

.card-content {
    display: flex;
    align-items: center;
    gap: 10px;
    flex-wrap: wrap; /* Enables wrapping in smaller sizes */
}

.card-info {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    flex: 1;
}

.card-title {
    font-size: 16px;
    font-weight: bold;
    color: #333;
    margin-bottom: 10px;
}

.percentage-circle {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 70px;
    height: 70px;
    border-radius: 50%;
    border: 5px solid #4CAF50; /* Default green border */
    transition: border-color 0.3s ease;
}

.card-percentage {
    font-size: 20px;
    font-weight: bold;
    color: #333;
}

.card-status {
    font-size: 14px;
    font-weight: bold;
    color: #333;
    margin-top: 5px;
}

.card-legend {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 5px;
    font-size: 12px;
    color: #333;
    margin-left: 10px;
}

.legend-item {
    display: flex;
    align-items: center;
}

.legend-color {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    margin-right: 6px;
}

descriptor.controllerScript:
self.onInit = function() {
    // Example safety percentage
    var safetyLevel = 85;  // Adjust this value as needed

    // Update the percentage in the card
    var percentageElement = document.getElementById('card-percentage');
    var statusElement = document.getElementById('card-status'); // Status text element
    var percentageCircleElement = document.getElementById('percentage-circle'); // Circle around percentage
    percentageElement.innerText = safetyLevel + '%';

    // Update circle border color and status text based on safety level
    if (safetyLevel >= 80) {
        percentageCircleElement.style.borderColor = '#4CAF50'; // Green border for good
        statusElement.innerText = 'Good';
    } else if (safetyLevel >= 60) {
        percentageCircleElement.style.borderColor = '#FFEB3B'; // Yellow border for moderate
        statusElement.innerText = 'Moderate';
    } else {
        percentageCircleElement.style.borderColor = '#F44336'; // Red border for poor
        statusElement.innerText = 'Poor';
    }
};

