descriptor.controllerScript:
var myBarChart;

self.onInit = function() {
    // Define the data for the chart
    var chartData = {
        labels: ['Fall Prevention', 'Fire', 'Caught Detection', 'Out of Geofence', 'Free Fall', 'SOS'],
        datasets: [
            {
                label: 'Open',
                backgroundColor: '#ff7043',  // Orange for "Open" alerts
                data: [4, 7, 55, 172, 0, 6]  // Open alert counts
            },
            {
                label: 'Assigned Open',
                backgroundColor: '#29b6f6',  // Blue for "Assigned Open" alerts
                data: [0, 0, 0, 112, 0, 0]  // Assigned Open alert counts
            },
            {
                label: 'Resolved',
                backgroundColor: '#66bb6a',  // Green for "Resolved" alerts
                data: [0, 0, 55, 212, 0, 0]  // Resolved alert counts
            }
        ]
    };

    // Chart.js options
    var options = {
        maintainAspectRatio: false,
        scales: {
            xAxes: [{
                stacked: true,  // Stack the bars
                ticks: {
                    beginAtZero: true
                }
            }],
            yAxes: [{
                stacked: true  // Stack the bars on the Y-axis as well
            }]
        },
        legend: {
            position: 'bottom',  // Legend at the bottom
            labels: {
                fontColor: '#333'
            }
        },
        title: {
            display: true,
            text: 'Alerts Status Count by Category',
            fontSize: 16,
            fontColor: '#333'
        }
    };

    // Select the canvas element and create the chart
    var canvasElement = $('#alertsBarChart', self.ctx.$container)[0];
    var canvasCtx = canvasElement.getContext('2d');
    myBarChart = new Chart(canvasCtx, {
        type: 'horizontalBar',  // Use horizontal bar chart type
        data: chartData,
        options: options
    });
    
    // Resize when needed
    self.onResize();
};

self.onResize = function() {
    myBarChart.resize();
};

// If you want to update data dynamically (e.g., with telemetry data)
self.onDataUpdated = function() {
    // Example for updating the chart dynamically if needed
    myBarChart.update();
};
