descriptor.templateCss:
.alert-widget {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%; /* You can adjust the width */
    padding: 20px;
    border-radius: 10px;
    background-color: #D2691E; /* Orange color */
    color: white;
    font-family: Arial, sans-serif;
    font-weight: bold;
    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
}

.alert-title {
    font-size: 18px;
    margin-bottom: 10px;
}

.alert-value {
    font-size: 36px;
}

descriptor.controllerScript:
self.onInit = function() {
    // Example static value for Open Alerts
    var openAlertCount = 260;  // Default value, can be changed

    // Update the value in the widget
    $('.open-alert .alert-value').text(openAlertCount);
};

self.onDataUpdated = function() {
    // Check if ThingsBoard has provided any telemetry data
    if (self.ctx.data && self.ctx.data.length > 0) {
        // Safely access the telemetry data for Open Alerts
        var dynamicOpenAlertCount = self.ctx.data[0]?.value || 0;  // Use fallback of 0 if no data
        // Update the value in the widget
        $('.open-alert .alert-value').text(dynamicOpenAlertCount);
    } else {
        console.error('No telemetry data available for Open Alerts.');
    }
};

