descriptor.templateCss:
.kpi-widget {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 10px; /* Reduced padding */
    border: 1px solid #ddd;
    border-radius: 8px;
    background-color: white;
    width: 100%; /* Make the widget take full width of its container */
    height: 100%; /* Make the widget take full height of its container */
    box-sizing: border-box; /* Include padding and border in the width and height */
    font-family: Arial, sans-serif;
}

.kpi-widget .icon {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 5px; /* Adjusted for tighter spacing */
}

.kpi-widget .icon img {
    max-width: 80px; /* Adjust image size if needed */
    height: auto;
}

.kpi-widget .value {
    font-size: 2em;
    font-weight: bold;
    color: green;
    margin: 0; /* Remove extra margin */
}

.kpi-widget .label {
    font-size: 1em;
    color: #666;
    margin: 0; /* Remove extra margin */
}

descriptor.controllerScript:
self.onInit = function() {
    function updateRecycledAmount() {
        const recycledAmount = Math.floor(500 + Math.random() * 100);
        document.getElementById("recycled-amount").innerText = recycledAmount;
    }

    updateRecycledAmount();
    setInterval(updateRecycledAmount, 5000);
};

