descriptor.templateCss:
.alert-widget {
    display: flex;
    align-items: center;
    background-color: white;
    border-radius: 10px;
    padding: 10px;
    width: 100%;
    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
    font-family: Arial, sans-serif;
    font-weight: bold;
    color: #555;
}

.fire-alert {
    background-color: #F4CCCC; /* Light red color for Fire Alerts */
}

.alert-icon {
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #F4CCCC; /* Icon background color */
    border-radius: 50%;
    width: 50px;
    height: 50px;
    margin-right: 15px;
    color: white;
    font-size: 18px;
    font-weight: bold;
}

.alert-content {
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.alert-title {
    font-size: 16px;
    color: #888;
}

.alert-value {
    font-size: 28px;
    color: #333;
}

descriptor.controllerScript:
self.onInit = function() {
    // Example static value for Fire Alerts
    var fireAlertCount = 0;  // Default value, can be changed

    // Update the value in the widget
    $('.fire-alert .alert-value').text(fireAlertCount);
};

self.onDataUpdated = function() {
    // Check if ThingsBoard has provided any telemetry data
    if (self.ctx.data && self.ctx.data.length > 0) {
        // Safely access the telemetry data for Fire Alerts
        var dynamicFireAlertCount = self.ctx.data[0]?.value || 0;  // Use fallback of 0 if no data
        // Update the value in the widget
        $('.fire-alert .alert-value').text(dynamicFireAlertCount);
    } else {
        console.error('No telemetry data available for Fire Alerts.');
    }
};

