.battery-status-card {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 300px;
    height: auto;
    padding: 10px;
    font-family: Arial, sans-serif;
    color: #333;
    background-color: transparent;
}

.battery-info {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
}

.card-title {
    font-size: 16px;
    font-weight: bold;
    color: #333;
    margin-bottom: 10px;
}

.battery-circle {
    position: relative;
    width: 100px;
    height: 100px;
}

.battery-icon-and-percentage {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    text-align: center;
    color: #333;
    font-weight: bold;
}

.battery-icon {
    width: 30px;
    height: auto;
    margin-bottom: 5px;
}

#batteryLevel {
    font-size: 16px;
}

.card-legend {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 5px;
    font-size: 12px;
    color: #333;
    margin-left: 10px;
}

.legend-item {
    display: flex;
    align-items: center;
}

.legend-color {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    margin-right: 6px;
}

descriptor.controllerScript:
self.onInit = function() {
    var batteryLevel = 90; // Adjust this value as needed (0 to 100)

    // Update the battery level in the card
    document.getElementById('batteryLevel').innerText = batteryLevel + '%';

    // Drawing the circular progress
    var canvas = document.getElementById('batteryCanvas');
    var context = canvas.getContext('2d');
    var startAngle = -0.5 * Math.PI; // Start at the top
    var endAngle = ((batteryLevel / 100) * 2 - 0.5) * Math.PI; // Calculate end angle based on battery level

    // Clear the canvas
    context.clearRect(0, 0, canvas.width, canvas.height);

    // Draw background circle
    context.beginPath();
    context.arc(canvas.width / 2, canvas.height / 2, 40, 0, 2 * Math.PI);
    context.lineWidth = 8;
    context.strokeStyle = '#e0e0e0'; // Light gray background
    context.stroke();

    // Draw battery level circle
    context.beginPath();
    context.arc(canvas.width / 2, canvas.height / 2, 40, startAngle, endAngle);
    context.lineWidth = 8;
    context.strokeStyle = batteryLevel >= 80 ? '#4CAF50' : batteryLevel >= 60 ? '#FFEB3B' : '#F44336';
    context.stroke();
};

