descriptor.templateCss:
.buildings-container {
   display: flex;
   justify-content: space-around; /* Space out the buildings evenly */
   align-items: flex-end; /* Align the buildings to the bottom */
   width: 100%; /* Full width of the parent container */
}

.building-wrapper {
   text-align: center; /* Center-align text for the building and labels */
}

.building {
   position: relative;
   width: 100px;
   height: 200px;
   border: 3px solid #000;
   background-color: #e0e0e0;
   overflow: hidden;
   margin: 0 10px; /* Add some spacing between buildings */
}

.roof {
   position: absolute;
   top: -10px;
   left: 15px;
   width: 70px;
   height: 10px;
   background-color: #555;
   clip-path: polygon(50% 0, 100% 100%, 0 100%);
}

.fill {
   position: absolute;
   bottom: 0;
   width: 100%;
   height: 0;
   background-color: #4CAF50;
   transition: height 0.5s ease;
   z-index: 1;
}

.windows {
   position: absolute;
   top: 20px;
   width: 100%;
   height: 100%;
   display: flex;
   flex-wrap: wrap;
   justify-content: space-around;
   padding: 10px;
   z-index: 2; /* Keep windows above the fill */
}

.window {
   width: 20px;
   height: 20px;
   background-color: #a0a0a0;
   margin: 5px;
   border-radius: 2px;
}

.percentage-label {
   margin-top: 5px; /* Space above the label */
   font-size: 18px;
   color: #333;
}

.building-name {
   font-size: 16px;
   font-weight: bold;
   color: #333;
   margin-top: 5px; /* Space above the name */
}

descriptor.controllerScript:
self.onInit = function() {
   // Initialize with default data for three buildings
   self.ctx.data = [
       { value: 45, buildingName: "Building A" },
       { value: 87, buildingName: "Building B" },
       { value: 10, buildingName: "Building C" }
   ];
   
   // Update each building's fill and name
   updateBuildingFill(1, self.ctx.data[0].value);
   updateBuildingName(1, self.ctx.data[0].buildingName);

   updateBuildingFill(2, self.ctx.data[1].value);
   updateBuildingName(2, self.ctx.data[1].buildingName);

   updateBuildingFill(3, self.ctx.data[2].value);
   updateBuildingName(3, self.ctx.data[2].buildingName);
};

self.onDataUpdated = function() {
   // Update fill height and building name when data changes
   for (var i = 0; i < self.ctx.data.length; i++) {
       updateBuildingFill(i + 1, self.ctx.data[i].value);
       updateBuildingName(i + 1, self.ctx.data[i].buildingName);
   }
};

function updateBuildingFill(index, percentage) {
   var fillElement = document.getElementById('building-fill-' + index);
   var labelElement = document.getElementById('percentage-label-' + index);
   
   // Set the fill height based on percentage
   fillElement.style.height = percentage + '%';
   
   // Update the label text
   labelElement.textContent = percentage + '%';
}

function updateBuildingName(index, name) {
   var nameElement = document.getElementById('building-name-' + index);
   nameElement.textContent = name;
}

