descriptor.controllerScript:
self.onInit = function() {
    setTimeout(function() {
        const canvas = document.getElementById('elevatorWorkerChart');

        if (!canvas) {
            console.error('Canvas element not found!');
            return;
        }

        const ctx = canvas.getContext('2d');

        // Sample data for elevator usage and worker efficiency
        const elevatorUsage = [20, 35, 55, 70, 85, 60, 45, 40, 65, 80, 75, 50];
        const workerEfficiency = elevatorUsage.map(usage => {
            if (usage >= 80) return 100 - usage * 0.7;
            else if (usage >= 50) return 90 - usage * 0.5;
            else return 95;
        });

        new Chart(ctx, {
            type: 'bar',
            data: {
                labels: ['7:30 AM', '8:30 AM', '9:30 AM', '10:30 AM', '11:30 AM', '12:30 PM', '1:30 PM', '2:30 PM', '3:30 PM', '4:30 PM', '5:30 PM', '6:30 PM'],
                datasets: [
                    {
                        type: 'bar',
                        label: 'Elevator Usage (%)',
                        data: elevatorUsage,
                        backgroundColor: 'rgba(204, 51, 255, 0.6)', // Light purple (#cc33ff)
                        borderColor: 'rgba(204, 51, 255, 1)',
                        borderWidth: 1,
                        yAxisID: 'y'
                    },
                    {
                        type: 'line',
                        label: 'Worker Efficiency (%)',
                        data: workerEfficiency,
                        borderColor: '#33ff33', // Light green (#33ff33)
                        borderWidth: 2,
                        fill: false,
                        pointRadius: 5,
                        pointBackgroundColor: '#33ff33',
                        yAxisID: 'y1'
                    }
                ]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                scales: {
                    y: {
                        beginAtZero: true,
                        max: 100,
                        position: 'left',
                        title: {
                            display: true,
                            text: 'Elevator Usage (%)',
                            font: {
                                weight: 'bold'
                            }
                        },
                        grid: {
                            color: 'rgba(153, 102, 255, 0.2)' // Light grid in line with theme colors
                        }
                    },
                    y1: {
                        beginAtZero: true,
                        max: 100,
                        position: 'right',
                        title: {
                            display: true,
                            text: 'Worker Efficiency (%)',
                            font: {
                                weight: 'bold'
                            }
                        },
                        grid: {
                            drawOnChartArea: false
                        }
                    },
                    x: {
                        title: {
                            display: true,
                            text: 'Time of Day',
                            font: {
                                weight: 'bold'
                            }
                        },
                        grid: {
                            color: 'rgba(153, 102, 255, 0.2)'
                        }
                    }
                },
                plugins: {
                    legend: {
                        display: true,
                        position: 'top',
                        labels: {
                            usePointStyle: true,
                            font: {
                                weight: 'bold'
                            }
                        }
                    },
                    tooltip: {
                        mode: 'index',
                        intersect: false,
                        backgroundColor: 'rgba(0, 0, 0, 0.7)',
                        titleFont: { weight: 'bold' },
                        bodyFont: { weight: 'normal' }
                    }
                },
                interaction: {
                    mode: 'index',
                    intersect: false
                }
            }
        });
    }, 500); // Delay to allow canvas loading
};

