descriptor.templateCss:
.site-map {
    font-family: Arial, sans-serif;
    margin: 10px;
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 10px;
}

.site {
    position: relative;
    border: 2px solid #ddd;
    padding: 20px;
    background-color: #e0ffe0; /* Green for allowed areas */
    text-align: center;
}

.site .restricted-area {
    margin-top: 10px;
    padding: 10px;
    border: 1px solid red;
    background-color: #ffe0e0; /* Red for restricted areas */
}

.site-label {
    font-weight: bold;
    margin-bottom: 10px;
}

.worker {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    display: inline-block;
    margin: 4px;
    position: relative;
    cursor: pointer;
}

.allowed-worker {
    background-color: green;
}

.prohibited-worker {
    background-color: red;
}

/* Tooltip styling */
.worker:hover::after {
    content: attr(data-info);
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    padding: 4px 8px;
    background-color: black;
    color: white;
    font-size: 10px;
    border-radius: 4px;
    white-space: nowrap;
    pointer-events: none;
}

