descriptor.templateCss:
.site-map {
  font-family: Arial, sans-serif;
  text-align: center;
  margin: 10px;
  padding: 20px;
}

h3 {
  color: #333;
}

.site {
  position: relative;
  margin: 10px auto;
  width: 150px;
  height: 150px;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  display: flex;
  justify-content: center;
  align-items: center;
}

.allowed {
  background-color: #e0ffe0;
  border: 2px solid #4CAF50;
}

.prohibited {
  background-color: #ffe0e0;
  border: 2px solid #FF5733;
}

.site-label {
  position: absolute;
  top: 8px;
  font-weight: bold;
}

.worker {
  width: 20px;
  height: 20px;
  border-radius: 50%;
}

.allowed-worker {
  background-color: #4CAF50;
}

.prohibited-worker {
  background-color: #FF5733;
}

