descriptor.templateCss:
.chart-container {
    position: relative;
    width: 100%;
    height: 300px;
    max-width: 500px;
    margin: auto;
    padding: 15px;
    background: #ffffff; /* پس‌زمینه سفید */
    border-radius: 10px;
    text-align: center;
}

.chart-title {
    font-size: 0.9em;
    font-weight: bold;
    margin-bottom: 10px;
    color: #333;
}

descriptor.controllerScript:
self.onInit = function() {
    // Load Chart.js script dynamically
    function loadScript(url, callback) {
        const script = document.createElement("script");
        script.type = "text/javascript";
        script.src = url;
        script.onload = callback;
        document.head.appendChild(script);
    }

    loadScript("https://cdn.jsdelivr.net/npm/chart.js", function() {
        const ctx = document.getElementById('dynamic-cost-prediction-chart').getContext('2d');

        // Fixed data for the chart (Construction-specific logical values)
        const labels = ["January", "February", "March", "April", "May", "June", "July"];
        const actualCosts = [15000, 18000, 17000, 20000, 22000, 24000, 26000];
        const predictedCosts = [15500, 18500, 17500, 21000, 23000, 25000, 27000];

        // Create the chart
        const costPredictionChart = new Chart(ctx, {
            type: 'bar',
            data: {
                labels: labels,
                datasets: [
                    {
                        label: "Actual Costs (TL)",
                        data: actualCosts,
                        backgroundColor: "#1a237e", // Dark blue for bars
                        borderColor: "#1a237e",
                        borderWidth: 1
                    },
                    {
                        label: "Predicted Costs (TL)",
                        data: predictedCosts,
                        borderColor: "#ffeb3b", // Yellow for predicted line
                        borderWidth: 2,
                        type: 'line',
                        fill: false,
                        tension: 0.4,
                        pointBackgroundColor: "#ffeb3b",
                        pointRadius: 4,
                        borderDash: [5, 5]
                    }
                ]
            },
            options: {
                responsive: true,
                maintainAspectRatio: true, // Maintain aspect ratio for full-screen view
                scales: {
                    y: {
                        beginAtZero: true,
                        grid: {
                            color: "#000000", // Black grid lines for better visibility
                            lineWidth: 1.5
                        },
                        title: {
                            display: true,
                            text: 'Cost (TL)',
                            color: '#333',
                            font: {
                                size: 12
                            }
                        },
                        ticks: {
                            stepSize: 5000,
                            color: '#333',
                            font: {
                                size: 10
                            }
                        }
                    },
                    x: {
                        grid: {
                            display: false
                        },
                        title: {
                            display: true,
                            text: 'Months',
                            color: '#333',
                            font: {
                                size: 12
                            }
                        },
                        ticks: {
                            color: '#333',
                            font: {
                                size: 10
                            }
                        }
                    }
                },
                plugins: {
                    legend: {
                        position: 'left',
                        labels: {
                            color: '#333',
                            font: {
                                size: 10
                            }
                        }
                    },
                    tooltip: {
                        enabled: true,
                        backgroundColor: '#f5f5f5',
                        titleColor: '#333',
                        bodyColor: '#333',
                        borderColor: '#007bff',
                        borderWidth: 1
                    }
                },
                layout: {
                    padding: {
                        left: 0,
                        right: 0,
                        top: 0,
                        bottom: 0
                    }
                }
            }
        });
    });
};

